import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate(); // useNavigate hook

  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await axios.post("http://localhost:5000/login", { email, password });

      if (response.data.success) {
        alert(response.data.message);
        navigate("/dashboard"); // navigate to Dashboard page
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      alert(error.response?.data?.message || "Error logging in");
    } finally {
      setIsLoading(false);
    }
  };

  const handleForgotPassword = async () => {
    const userEmail = prompt("Enter your registered email:");
    if (!userEmail) return;

    try {
      const response = await axios.post("http://localhost:5000/forgot-password", { email: userEmail });
      alert(response.data.message);
    } catch (error) {
      alert(error.response?.data?.message || "Error resetting password");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4 font-inter">
      <div className="w-full max-w-sm bg-white rounded-xl shadow-2xl p-8">
        {/* Login form UI */}
        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label>Email Address</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div className="mb-4">
            <label>Password</label>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          <button type="button" onClick={handleForgotPassword}>Forgot password?</button>
          <button type="submit" disabled={isLoading}>{isLoading ? "Loading..." : "Sign In"}</button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
