import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  // Handle login
  const handleLogin = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      const response = await axios.post("http://localhost:5000/login", { email, password });
      if (response.data.success) {
        alert(response.data.message);
        navigate("/dashboard"); // Navigate to Dashboard
      } else {
        alert(response.data.message);
      }
    } catch (error) {
      alert(error.response?.data?.message || "Error logging in");
    } finally {
      setIsLoading(false);
    }
  };

  // Handle forgot password
  const handleForgotPassword = async () => {
    const userEmail = prompt("Enter your registered email:");
    if (!userEmail) return;

    try {
      const response = await axios.post("http://localhost:5000/forgot-password", { email: userEmail });
      alert(response.data.message);
    } catch (error) {
      alert(error.response?.data?.message || "Error resetting password");
    }
  };

  // Navigate to registration page
  const handleNewUser = () => {
    navigate("/register");
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4 font-inter">
      <div className="w-full max-w-sm bg-white rounded-xl shadow-2xl p-8">
        <div className="flex flex-col items-center mb-6">
          <div className="w-12 h-12 bg-gray-900 rounded-full flex items-center justify-center mb-4">
            <span className="text-white text-xl font-bold">TK</span>
          </div>
          <h1 className="text-2xl font-bold text-gray-800 mb-2">Login to your account</h1>
          <p className="text-sm text-gray-500">Enter your details to proceed</p>
        </div>

        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-semibold mb-2">Email / Username</label>
            <input
              type="email"
              placeholder="you@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="border rounded-lg w-full py-3 px-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div className="mb-6">
            <label className="block text-gray-700 text-sm font-semibold mb-2">Password</label>
            <input
              type="password"
              placeholder="Enter password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="border rounded-lg w-full py-3 px-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
              required
            />
          </div>

          <div className="flex justify-between mb-4">
            <button
              type="button"
              onClick={handleForgotPassword}
              className="text-blue-500 text-sm hover:text-blue-800"
            >
              Forgot password?
            </button>
            <button
              type="button"
              onClick={handleNewUser}
              className="text-green-500 text-sm hover:text-green-700"
            >
              New User? Sign Up
            </button>
          </div>

          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-4 rounded-lg"
            disabled={isLoading}
          >
            {isLoading ? "Loading..." : "Login"}
          </button>
        </form>
      </div>
    </div>
  );
}
