import React, { useState } from 'react';
// Assume Tailwind CSS is available in the environment, so no import needed.

const App = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleLogin = (e) => {
    e.preventDefault();
    setIsLoading(true);
    // Simulate a network request
    setTimeout(() => {
      console.log('Attempting to log in with:', { email, password });
      setIsLoading(false);
      // In a real application, you would handle authentication here.
      // For now, we'll just log the credentials.
    }, 1500);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4 font-inter">
      <div className="w-full max-w-sm bg-white rounded-xl shadow-2xl p-8 transform transition-transform duration-300 hover:scale-105">
        <div className="flex flex-col items-center">
          {/* Technokraft logo placeholder */}
          <div className="w-12 h-12 bg-gray-900 rounded-full flex items-center justify-center mb-4">
            <span className="text-white text-xl font-bold">TK</span>
          </div>
          <h1 className="text-2xl font-bold text-gray-800 mb-2">Login to your account</h1>
          <p className="text-sm text-gray-500 mb-6">Enter your details to proceed</p>
        </div>

        <form onSubmit={handleLogin}>
          <div className="mb-4">
            <label className="block text-gray-700 text-sm font-semibold mb-2" htmlFor="email">
              Email Address
            </label>
            <input
              className="appearance-none border rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
              id="email"
              type="email"
              placeholder="you@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="mb-6">
            <label className="block text-gray-700 text-sm font-semibold mb-2" htmlFor="password">
              Password
            </label>
            <input
              className="appearance-none border rounded-lg w-full py-3 px-4 text-gray-700 leading-tight focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
              id="password"
              type="password"
              placeholder=""
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <div className="flex items-center justify-between mb-4">
            <a href="#" className="inline-block align-baseline font-semibold text-sm text-blue-500 hover:text-blue-800 transition-colors duration-300">
              Forgot password?
            </a>
          </div>
          <button
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-4 rounded-lg focus:outline-none focus:shadow-outline transition-all duration-300 transform hover:scale-100"
            type="submit"
            disabled={isLoading}
          >
            {isLoading ? (
              <svg className="animate-spin h-5 w-5 text-white mx-auto" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
            ) : 'Sign In'}
          </button>
        </form>
        <div className="mt-6 text-center">
          <p className="text-gray-500 text-sm">
            Don't have an account? <a href="#" className="font-semibold text-blue-500 hover:text-blue-800 transition-colors duration-300">Sign Up</a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default App;
